def cloudinary(img, format, version)
  resource_type = 'image'
  type = 'upload'
  public_id = img
  signature = Cloudinary::Utils.api_sign_request({ public_id: public_id, version: version}, ENV['CLOUDINARY_API_SECRET'])
  "#{resource_type}/#{type}/v#{version}/#{public_id}.#{format}##{signature}"
end

